import { Subject } from 'rxjs';
import { debounceTime } from 'rxjs/operators';
import * as i0 from "@angular/core";
export class AnnotationOSDService {
    /**
     * Constructor of service for init a new player.
     * @param debug set true to active debug mode on player.
     */
    constructor(debug = false) {
        this.DEBUG = debug;
    }
    /**
     * Create a new player factory.
     * @param type Type of player, dzi or image (jpeg, png, etc...)
     * @param id Id of player inside html.
     * @param link Link to image.
     */
    playerFactory(type, id, link) {
        return new Promise((resolve) => {
            this.viewer = OpenSeadragon({
                id,
                tileSources: type === 'dzi' ? link : { type: 'image', url: link },
                gestureSettingsTouch: { pinchRotate: false },
                showRotationControl: false,
                showNavigationControl: false,
                showFlipControl: false,
                zoomPerClick: 1,
                imageLoaderLimit: 1,
                constrainDuringPan: true,
                debugMode: this.DEBUG,
            });
            const subjectZoom = new Subject();
            this.viewer.addHandler('zoom', (e) => {
                subjectZoom.next(e);
            });
            subjectZoom.pipe(debounceTime(50)).subscribe(() => {
                this.calculateRatio();
                this.resetStrokeWidth();
            });
            this.viewer.addOnceHandler('open', () => {
                this.calculateRatio();
                resolve(this);
            });
            const subjectResize = new Subject();
            this.viewer.addHandler('resize', (e) => {
                subjectResize.next(e);
            });
            subjectResize.pipe(debounceTime(200)).subscribe(() => {
                this.goHome();
            });
        });
    }
    /**
     * Delete all subscribers canvas and player.
     */
    delete() {
        this.canvas.clear();
        this.canvas.dispose();
        this.canvas.removeListeners();
        this.viewer.destroy();
    }
    /**
     * Add fabric canvas over viewport
     */
    addCanvas() {
        this.canvas = this.viewer
            .fabricjsOverlay({ scale: 1000 })
            .fabricCanvas();
        return this;
    }
    /**
     * Auto reset the size of fabric elements when user zoom on canvas, or page resize.
     * @private
     */
    resetStrokeWidth() {
        if (this.canvas) {
            const w = 2; // desired width in pixels
            const strokeWidth = w / this.canvas.getZoom();
            for (const object of this.canvas.getObjects()) {
                object.set('strokeUniform', true);
                object.set('strokeWidth', strokeWidth);
                // @ts-ignore
                if (object.get('radius')) {
                    // @ts-ignore
                    object.set('radius', 5 / this.canvas.getZoom());
                }
                // can also update other things here
            }
        }
    }
    /**
     * Calculate ratio of canvas when user load image or resize page.
     * @private
     */
    calculateRatio() {
        this.imageWidth = this.viewer.source.dimensions.x;
        this.imageHeight = this.viewer.source.dimensions.y;
        this.containerWidth = this.viewer.viewport.getContainerSize().x;
        this.containerHeight = this.viewer.viewport.getContainerSize().y;
        this.zoomToZoomLevelRatio = this.containerWidth / this.imageWidth;
        this.scale = this.viewer.viewport.getZoom(true) * this.zoomToZoomLevelRatio;
    }
    /**
     * Center canvas inside viewport or go to home point if exist.
     */
    async goHome() {
        //this.viewer.viewport.goHome(false);
        return this;
    }
    /**
     * Set home point.
     * @param point Point at center of region.
     * @param width Width of region.
     * @param height Height of region.
     */
    setHome(point, width, height) {
        this.viewer.viewport.zoomTo(5);
        this.viewer.viewport.windowToViewportCoordinates(new OpenSeadragon.Point(100, 100));
        // this.viewer.viewport.zoomTo(5);
        // const homePoint = this.viewer.viewport.imageToViewerElementCoordinates(new OpenSeadragon.Point(this.imageWidth / 2, this.imageHeight / 2));
        // const w = this.imageWidth / width;
        // const h = this.imageHeight / height;
        // const zoom = 4; // w > h ? h : w;
        // console.log(homePoint, zoom, this.imageWidth, this.imageHeight);
        // this.viewer.viewport.zoomTo(1.2, homePoint, false);
        return this;
    }
}
