import { AnnotationOSDService } from '../../../annotation-osd/src/lib/annotation-osd.service';
import { MarkerPoly, MarkerRect, MarkerRound, } from '../../../annotation-osd/src/lib/annotation-osd.model';
import * as i0 from "@angular/core";
export class AppComponent {
    async ngOnInit() {
        let options = {
            style: {
                hover: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'red',
                    opacity: 0.2,
                },
                active: {
                    fill: 'transparent',
                    cornerColor: 'pink',
                    stroke: 'pink',
                    opacity: 1,
                },
                neutral: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'blue',
                    opacity: 1,
                },
            },
            selectable: true,
            hasBorders: false,
            transparentCorners: false,
            draggable: true,
            lockRotation: true,
            hasControls: true,
        };
        const damage1 = new MarkerRound('damage1test', options, 0.2, 0.5, 0.1);
        const damage2 = new MarkerRect('damage2test', options, 0.5, 0.5, 0.1, 0.1);
        const damage3 = new MarkerPoly('damage3test', options, [{ x: 0.1, y: 0.2 }, { x: 0.1, y: 0.4 }, { x: 0.5, y: 0.6 }]);
        let dam3 = {
            "id": "new3",
            "options": {
                "style": {
                    "hover": {
                        "fill": "transparent",
                        "cornerColor": "red",
                        "stroke": "red",
                        "opacity": 0.2
                    },
                    "active": {
                        "fill": "transparent",
                        "cornerColor": "pink",
                        "stroke": "pink",
                        "opacity": 1
                    },
                    "neutral": {
                        "fill": "transparent",
                        "cornerColor": "red",
                        "stroke": "green",
                        "opacity": 1
                    }
                },
                "selectable": true,
                "hasBorders": false,
                "transparentCorners": false,
                "draggable": true,
                "lockRotation": true,
                "hasControls": true
            },
            "unit": "percent",
            "centerY": 0.4079736292357362,
            "centerX": 0.7363737486095662,
            "width": 0.14794215795328153,
            "height": 0.09359993488700183,
            "type": "MarkerRect"
        };
        const player = new AnnotationOSDService(true);
        player.objectActions.subscribe((e) => {
            console.log('subscriber :: ', e);
            if (player.getSelectedObjectId())
                console.log('object selected :: ', player.getSelectedObjectId());
        });
        await player.playerFactory('image', 'player', 'https://www.auto-moto.com/wp-content/uploads/sites/9/2022/02/01-peugeot-208-750x410.jpg');
        // player.setHome({ point : { x : 0.2, y:0.5 }, width : 0.5, height: 0.5 });
        player.addCanvas();
        player.addMarker(damage1);
        player.addMarker(damage2);
        player.addMarker(damage3);
        player.setDrawMode({
            isDrawing: true,
            options: options,
            unit: 'percent',
            type: 'MarkerRect',
            data: { id: 'new3' },
        });
        setTimeout(() => {
            player.setDrawMode({
                isDrawing: true,
                options: options,
                unit: 'percent',
                type: 'MarkerRound',
                data: { id: 'new4' },
            });
        }, 10000);
    }
}
