import { AnnotationOSDService } from '../../../annotation-osd/src/lib/annotation-osd.service';
import { MarkerPoly, MarkerRect, MarkerRound, } from '../../../annotation-osd/src/lib/annotation-osd.model';
import * as i0 from "@angular/core";
export class AppComponent {
    async ngOnInit() {
        let options = {
            style: {
                hover: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'red',
                    opacity: 0.2,
                },
                active: {
                    fill: 'transparent',
                    cornerColor: 'pink',
                    stroke: 'pink',
                    opacity: 1,
                },
                neutral: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'blue',
                    opacity: 1,
                },
            },
            selectable: true,
            hasBorders: false,
            transparentCorners: false,
            draggable: true,
            lockRotation: true,
            hasControls: true,
        };
        const damage1 = new MarkerRound('damage1test', options, 0.2, 0.5, 0.1);
        const damage2 = new MarkerRect('damage2test', options, 0.5, 0.5, 0.1, 0.1);
        const damage3 = new MarkerPoly('damage3test', options, [{ x: 0.1, y: 0.2 }, { x: 0.1, y: 0.4 }, { x: 0.5, y: 0.6 }]);
        const player = new AnnotationOSDService(true);
        await player.playerFactory('image', 'player', 'https://www.auto-moto.com/wp-content/uploads/sites/9/2022/02/01-peugeot-208-750x410.jpg');
        player.setHome({ point: { x: 0.2, y: 0.5 }, width: 0.5, height: 0.5 });
        player.addCanvas().addMarker(damage1).addMarker(damage2).addMarker(damage3);
        player.setDrawMode({
            isDrawing: true,
            options: options,
            unit: 'percent',
            type: 'MarkerRound',
            data: { id: 't1' },
        });
        player.objectActions.subscribe((e) => {
            console.log('subscriber :: ', e);
            if (e.type === 'createMarker' && e.object instanceof MarkerRect) {
                options.style.neutral.stroke = 'red';
                const d = new MarkerRect('t2', options, e.object.centerX, e.object.centerY, e.object.width, e.object.height, e.object.unit);
                player.addMarker(d);
            }
            if (e.type === 'before:selection:cleared' && e.object instanceof MarkerRect) {
                options.style.neutral.stroke = 'yellow';
                const d = new MarkerRect('t1', options, e.object.centerX, e.object.centerY, e.object.width, e.object.height, e.object.unit);
                player.addMarker(d);
            }
            if (e.type === 'createMarker' && e.object instanceof MarkerRound) {
                options.style.neutral.stroke = 'red';
                const d = new MarkerRound('t2', options, e.object.centerX, e.object.centerY, e.object.height, e.object.unit);
                player.addMarker(d);
            }
            if (e.type === 'before:selection:cleared' && e.object instanceof MarkerRound) {
                options.style.neutral.stroke = 'yellow';
                const d = new MarkerRound('t3', options, e.object.centerX, e.object.centerY, e.object.height, e.object.unit);
                player.addMarker(d);
            }
            if (player.getSelectedObjectId())
                console.log('object selected :: ', player.getSelectedObjectId());
        });
        // setTimeout(()=>{
        //     player.setDrawMode({
        //         isDrawing : true,
        //         options : options,
        //         unit : 'percent',
        //         type :  'MarkerRound',
        //         data : { id : 'new4' },
        //     });
        // }, 10000);
    }
}
