import { AnnotationOSDService } from '../../../annotation-osd/src/lib/annotation-osd.service';
import { MarkerPoly, } from '../../../annotation-osd/src/lib/annotation-osd.model';
import * as i0 from "@angular/core";
export class AppComponent {
    async ngOnInit() {
        let options = {
            style: {
                hover: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'red',
                    opacity: 0.2,
                },
                active: {
                    fill: 'transparent',
                    cornerColor: 'pink',
                    stroke: 'pink',
                    opacity: 1,
                },
                neutral: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'blue',
                    opacity: 1,
                },
            },
            selectable: true,
            hasBorders: false,
            transparentCorners: false,
            draggable: true,
            lockRotation: true,
            hasControls: true,
        };
        //const damage1 = new MarkerRound('marker1', options, 0.2, 0.5, 0.1);
        //const damage2 = new MarkerRect('marker2',  options, 0, 0, 0.2, 0.2 );
        const damage3 = new MarkerPoly('marker3', options, [{ x: 2, y: 2 }, { x: 200, y: 200 }, { x: 110, y: 10 }], 'pixel');
        const player = new AnnotationOSDService(true);
        await player.playerFactory('image', 'player', 'https://www.auto-moto.com/wp-content/uploads/sites/9/2022/02/01-peugeot-208-750x410.jpg');
        // player.setHome({ point : { x : 0.2, y:0.5 }, width : 0.5, height: 0.5 });
        player.addCanvas()
            //.addMarker(damage1)
            //  .addMarker(damage2)
            .addMarker(damage3);
        // player.setDrawMode({
        //     isDrawing : true,
        //     options : options,
        //     unit : 'percent',
        //     type :  'MarkerPoly',
        //     data : { id : 'test1' },
        // });
        player.objectActions.subscribe((e) => {
            console.log('subscriber :: ', e);
        });
    }
}
