import { AnnotationOSDService } from '../../../annotation-osd/src/lib/annotation-osd.service';
import { MarkerPoly, MarkerRect, MarkerRound } from '../../../annotation-osd/src/lib/annotation-osd.model';
import * as i0 from "@angular/core";
export class AppComponent {
    async ngOnInit() {
        const damage1 = new MarkerRound('damage1test', {
            style: {
                hover: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'red',
                    opacity: 0.2,
                },
                active: {
                    fill: 'transparent',
                    cornerColor: 'pink',
                    stroke: 'pink',
                    opacity: 1,
                },
                neutral: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'blue',
                    opacity: 1,
                },
            },
            selectable: true,
            hasBorders: false,
            transparentCorners: false,
            draggable: true,
            lockRotation: true,
            hasControls: true,
        }, 0.2, 0.5, 0.1);
        const damage2 = new MarkerRect('damage2test', {
            style: {
                hover: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'red',
                    opacity: 0.2,
                },
                active: {
                    fill: 'transparent',
                    cornerColor: 'pink',
                    stroke: 'pink',
                    opacity: 1,
                },
                neutral: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'blue',
                    opacity: 1,
                },
            },
            selectable: true,
            hasBorders: false,
            transparentCorners: false,
            draggable: true,
            lockRotation: true,
            hasControls: true,
        }, 0.3, 0.6, 0.1, 0.1);
        const damage3 = new MarkerPoly('damage3test', {
            style: {
                hover: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'red',
                    opacity: 0.2,
                },
                active: {
                    fill: 'transparent',
                    cornerColor: 'pink',
                    stroke: 'pink',
                    opacity: 1,
                },
                neutral: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'blue',
                    opacity: 1,
                },
            },
            selectable: true,
            hasBorders: false,
            transparentCorners: false,
            draggable: true,
            lockRotation: true,
            hasControls: true,
        }, [{ x: 0.1, y: 0.2 }, { x: 0.1, y: 0.4 }, { x: 0.5, y: 0.6 }]);
        const player = new AnnotationOSDService(true);
        player.objectActions.subscribe((e) => {
            console.log('comp subscriber', e);
            console.log('object selected : ', player.getSelectedObjectId());
        });
    }
    ;
}
