import { AnnotationOSDService } from '../../../annotation-osd/src/lib/annotation-osd.service';
import { MarkerPoly, MarkerRect, MarkerRound, } from '../../../annotation-osd/src/lib/annotation-osd.model';
import * as i0 from "@angular/core";
export class AppComponent {
    async ngOnInit() {
        let options = {
            style: {
                hover: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'red',
                    opacity: 0.2,
                },
                active: {
                    fill: 'transparent',
                    cornerColor: 'pink',
                    stroke: 'pink',
                    opacity: 1,
                },
                neutral: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'blue',
                    opacity: 1,
                },
            },
            selectable: true,
            hasBorders: false,
            transparentCorners: false,
            draggable: true,
            lockRotation: true,
            hasControls: true,
        };
        const damage1 = new MarkerRound('marker1', options, 0.2, 0.5, 0.1);
        const damage2 = new MarkerRect('marker2', options, 0.5, 0.5, 0.1, 0.1);
        const damage3 = new MarkerPoly('marker3', options, [{ x: 0.1, y: 0.2 }, { x: 0.1, y: 0.4 }, { x: 0.5, y: 0.6 }]);
        const player = new AnnotationOSDService(true);
        await player.playerFactory('image', 'player', 'https://www.auto-moto.com/wp-content/uploads/sites/9/2022/02/01-peugeot-208-750x410.jpg');
        // player.setHome({ point : { x : 0.2, y:0.5 }, width : 0.5, height: 0.5 });
        player.addCanvas().addMarker(damage1).addMarker(damage2).addMarker(damage3);
        player.setDrawMode({
            isDrawing: true,
            options: options,
            unit: 'percent',
            type: 'MarkerPoly',
            data: { id: 'test1' },
        });
        player.objectActions.subscribe((e) => {
            console.log('subscriber :: ', e);
            // @ts-ignore
            let aaa = structuredClone(e);
            aaa.object.id = 'aaa';
            console.log(aaa.object);
            player.addMarker(aaa.object);
        });
    }
}
