import { AnnotationOSDService } from '../../../annotation-osd/src/lib/annotation-osd.service';
import { MarkerRound } from '../../../annotation-osd/src/lib/annotation-osd.model';
import * as i0 from "@angular/core";
export class AppComponent {
    async ngOnInit() {
        const player = new AnnotationOSDService(true);
        player.objectActions.subscribe((e) => {
            console.log('comp subscriber', e);
        });
        await player.playerFactory('image', 'player', 'https://www.auto-moto.com/wp-content/uploads/sites/9/2022/02/01-peugeot-208-750x410.jpg');
        player.setHome({ point: { x: 0.2, y: 0.5 }, width: 0.5, height: 0.5 });
        player.addCanvas();
        const damage = new MarkerRound('test', {
            style: {
                hover: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'red',
                    opacity: 1,
                },
                active: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'pink',
                    opacity: 1
                },
                neutral: {
                    fill: 'transparent',
                    cornerColor: 'red',
                    stroke: 'blue',
                    opacity: 1
                },
            },
            selectable: true,
            hasBorders: false,
            transparentCorners: false,
            draggable: true,
            lockRotation: true,
            hasControls: true,
        }, 0.2, 0.5, 0.1);
        player.addDamage(damage);
    }
}
